import React, {Component} from 'react';
import axios from 'axios';
class App extends Component {

    state = {
        todosBackup: [],
        todos: [],
        users: [],
        searchTerm: "",
    }

    componentWillMount() {
        this._getTodos()
            .then(this._getUsers())
    }

    _getTodos() {
        return axios.get('https://jsonplaceholder.typicode.com/todos').then((response) => {
            let todos = response.data.map((todo) => {
                todo.displayTitle = this.displayTitle(todo.Title, '');
                return todo
            })

            this.setState({
                todos: todos,
                todosBackup: todos
            })
        });
    }

    _getUsers() {
        axios.get('https://jsonplaceholder.typicode.com/users').then((response) => {
            this.setState({
                users: response.data
            })
        });
    }

    function

    displayTitle() {
        return {__html: 'First &middot; <strong>Second</strong>'};
    }

    _formatTodosListing() {
        let todos = this.state.todos.map((todo) => {

            var user = this.state.users.find(function (user) {
                return user.id === todo.userId;
            });

            todo.userName = (user && user.name) ? user.name : "Unknown";

            return (
                <tr key={todo.id}>
                    <th>{todo.id}</th>
                    <th>{todo.userName}</th>
                    <th dangerouslySetInnerHTML={todo.displayTitle()}></th>
                    <th>{todo.completed ? "Yes" : "No"}</th>
                    <th>
                        <button className="btn btn-danger"
                                onClick={this.deleteTodo.bind(this, todo.id)}>Delete
                        </button>
                    </th>
                </tr>
            )
        });
        return todos;
    }

    search(event) {

        let todos = this.state.todosBackup;
        if (event.target.value.length > 0) {
            todos = todos.map((todo) => {
                todo.displayTitle = this.displayTitle(todo.Title, event.target.value);//() => ({__html: todo.Title});//todo.title.replace(new RegExp(event.target.value, 'g'), '<strong>' + event.target.value + '</strong>');
                return todo
            })
        } else {
            todos = todos.map((todo) => {
                todo.displayTitle = this.displayTitle(todo.Title, '')
                return todo
            })
        }
        console.log(todos);
        this.setState({
            todos: todos
        });

        //console.log(event.target.value.length)
        //console.log(this.state.todos)
        //console.log(this.state.todosBackup)
        /*
         if(event.target.value.length === 1 && !this.state.todosBackup.length) {
         let todos = this.state.todos
         this.setState({
         todosBackup: todos
         })
         ///
         console.log("backing up todos")
         } else if (!event.target.value.length && this.state.todosBackup.length) {
         let todosBackup = this.state.todosBackup;
         this.setState({
         todos: todosBackup,
         todosBackup: []
         })
         console.log("returning todos")
         }*/
        /*else {
         let todos = this.state.todos.map((todo) => {
         todo.displayTitle = todo.title.replace(new RegExp(event.target.value, 'g'), '<strong>'+event.target.value+'</strong>');
         return todo
         })
         this.setState({
         todos: todos
         });
         }*/
        //console.log("test: " + event.target.value);
        //console.log(this.state.todos)
        //console.log(this.state.todosBackup)


    }

    deleteTodo(id) {

        let todos = this.state.todos.filter(function (todo) {
            return todo.id !== id
        });
        this.setState({
            todos: todos
        });
    }

    render() {

        let todos = this._formatTodosListing();

        /*
         let users = this.state.users.map((user) => {
         return (
         <h5>{user.name}</h5>
         )
         });
         */
        /*
         {users}
         */

        return (

            <div className="App container">

                <input type="text" placeholder="Search" onChange={this.search.bind(this)}/>
                <table>
                    <thead>
                    <tr>
                        <th>ID</th>
                        <th>User</th>
                        <th>Title</th>
                        <th>Completed</th>
                        <th>Actions</th>
                    </tr>
                    </thead>
                    <tbody>
                    {todos}
                    </tbody>
                </table>
            </div>
        )
            ;
    }
}

export default App;
