import React, {Component} from 'react';
import axios from 'axios';
class App extends Component {

    state = {
        todos: [],
        users: [],
        searchTerm: ""
    }

    componentWillMount() {
        this._getTodos()
            .then(this._getUsers())
    }

    _getTodos() {
        return axios.get('https://jsonplaceholder.typicode.com/todos').then((response) => {
            this.setState({
                todos: response.data
            })
        });
    }

    _getUsers() {
        axios.get('https://jsonplaceholder.typicode.com/users').then((response) => {
            this.setState({
                users: response.data
            })
        });
    }

    _formatTodosListing() {
        let todos = this.state.todos.map((todo) => {

            var user = this.state.users.find(function (user) {
                return user.id === todo.userId;
            });
            console.log(user);

            todo.userName = (user && user.name) ? user.name : "Unknown";

            return (
                <tr>
                    <th>{todo.id}</th>
                    <th>{todo.userId} {todo.userName}</th>
                    <th>{todo.title}</th>
                    <th>{todo.completed}</th>
                    <th>
                        <button className="btn btn-danger"
                                onClick={this.deleteTodo.bind(this, todo.id)}>Delete
                        </button>
                    </th>
                </tr>
            )
        });

        return todos;
    }

    search(searchValue) {
        console.log("test: " + searchValue);
    }
    deleteTodo(id) {

        let todos = this.state.todos.filter(function (todo) {
            return todo.id !== id
        });
        this.setState({
            todos: todos
        });
    }

    render() {

        let todos = this._formatTodosListing();

        let users = this.state.users.map((user) => {
            return (
                <h5>{user.name}</h5>
            )
        });

        return (

            <div className="App container">
                {users}
                <input type="text" placeholder="Search" onChange={this.search}/>
                <table>
                    <thead>
                    <tr>
                        <th>ID</th>
                        <th>User</th>
                        <th>Rating</th>
                        <th>Completed</th>
                        <th>Actions</th>
                    </tr>
                    </thead>
                    <tbody>
                    {todos}
                    </tbody>
                </table>
            </div>
        )
            ;
    }
}

export default App;
