import React, {Component} from 'react';
import axios from 'axios';
class App extends Component {

    state = {
        todos: [],
        users: []
    }

    componentWillMount() {
        this._getTodos()
    }

    _getTodos() {
        axios.get('https://jsonplaceholder.typicode.com/todos').then((response) => {
            this.setState({
                todos: response.data
            })
        });
    }

    deleteTodo(id) {

        let todos = this.state.todos.filter( function (todo) {
            return todo.id !== id
        });
        this.setState({
            todos: todos
        });
    }

    render() {

        let todos = this.state.todos.map((todo) => {
            return (
                <tr>
                    <th>{todo.id}</th>
                    <th>{todo.userId}</th>
                    <th>{todo.title}</th>
                    <th>{todo.completed}</th>
                    <th>
                        <button className="btn btn-danger"
                        onClick={this.deleteTodo.bind(this, todo.id)}>Delete</button>
                    </th>
                </tr>
            )
        });

        let users = this.state.users.map((user) => {
            return (
                <h1>{user}</h1>
            )
        });

        return (

            <div className="App container">
                {users}
                <table>
                    <thead>
                    <tr>
                        <th>ID</th>
                        <th>User</th>
                        <th>Rating</th>
                        <th>Completed</th>
                        <th>Actions</th>
                    </tr>
                    </thead>
                    <tbody>
                    {todos}
                    </tbody>
                </table>
            </div>
        )
            ;
    }
}

export default App;
