import React, {Component} from 'react';
import axios from 'axios';
class App extends Component {

    state = {
        todos: [],
        users: []
    }

    componentWillMount() {
        this._getTodos().then(() => {
            this._getUsers();
        }).then(() => {
            this._formatTodos();
        });
    }

    _getTodos() {
        return axios.get('https://jsonplaceholder.typicode.com/todos').then((response) => {
            this.setState({
                todos: response.data
            })
        });
    }

    _getUsers() {
        axios.get('https://jsonplaceholder.typicode.com/users').then((response) => {
            this.setState({
                users: response.data
            })
        });
    }

    _formatTodos() {
        console.log('test');
        /*
        let todos = this.state.todos.map((todo) => {
            console.log(todo.userId);
            console.log(this.state.users);
            var user = this.state.users.find(function (element) {
                console.log(element);
                return true;
            });
            console.log(user);

            if (user && user.name) {
                todo.userName = user.name;
            }

            return todo
        });
        this.setState({
            todos: todos
        })
        */
    }

    deleteTodo(id) {

        let todos = this.state.todos.filter( function (todo) {
            return todo.id !== id
        });
        this.setState({
            todos: todos
        });
    }

    render() {

        let todos = this.state.todos.map((todo) => {
            return (
                <tr>
                    <th>{todo.id}</th>
                    <th>{todo.userName}</th>
                    <th>{todo.title}</th>
                    <th>{todo.completed}</th>
                    <th>
                        <button className="btn btn-danger"
                        onClick={this.deleteTodo.bind(this, todo.id)}>Delete</button>
                    </th>
                </tr>
            )
        });

        let users = this.state.users.map((user) => {
            return (
                <h5>{user.name}</h5>
            )
        });

        return (

            <div className="App container">
                {users}
                <table>
                    <thead>
                    <tr>
                        <th>ID</th>
                        <th>User</th>
                        <th>Rating</th>
                        <th>Completed</th>
                        <th>Actions</th>
                    </tr>
                    </thead>
                    <tbody>
                    {todos}
                    </tbody>
                </table>
            </div>
        )
            ;
    }
}

export default App;
