import React, {Component}  from 'react';
import ReactDOM from 'react-dom';
//import './index.css';
import * as serviceWorker from './serviceWorker';

//import React, {Component} from 'react';
import axios from 'axios';
class App extends Component {

    state = {
        todosBackup: [],
        todos: [],
        users: [],
        searchTerm: "",
    }

    componentWillMount() {
        this._getTodos()
            .then(this._getUsers())
    }

    _getTodos() {
        return axios.get('https://jsonplaceholder.typicode.com/todos').then((response) => {

            let todos = response.data;
            this.setState({
                todos: todos,
                todosBackup: todos
            })
        });
    }

    _getUsers() {
        axios.get('https://jsonplaceholder.typicode.com/users').then((response) => {
            this.setState({
                users: response.data
            })
        });
    }

    createMarkup(title) {

        let titleSearchHighlighted = title;
        if (this.state.searchTerm.length > 0) {
            titleSearchHighlighted = titleSearchHighlighted.replace(
                new RegExp(this.state.searchTerm, 'g'),
                '<span class="searchHighlight">' + this.state.searchTerm + '</span>'
            );
        }

        return {__html: titleSearchHighlighted};
    }

    _formatTodosListing() {
        let todos = this.state.todos.map((todo) => {

            var user = this.state.users.find(function (user) {
                return user.id === todo.userId;
            });

            todo.userName = (user && user.name) ? user.name : "Unknown";

            return (
                <tr key={todo.id}>
                    <th>{todo.id}</th>
                    <th>{todo.userName}</th>
                    <th dangerouslySetInnerHTML={this.createMarkup(todo.title)}></th>
                    <th>{todo.completed ? "Yes" : "No"}</th>
                    <th>
                        <button className="btn btn-danger"
                                onClick={this.deleteTodo.bind(this, todo.id)}>Delete
                        </button>
                    </th>
                </tr>
            )
        });
        return todos;
    }

    search(event) {

        this.setState({
            searchTerm: event.target.value
        });

        let todos = this.state.todosBackup.filter(function (todo) {
            return todo.title.includes(event.target.value)
        });
        this.setState({
            todos: todos
        });
    }

    deleteTodo(id) {

        let todos = this.state.todos.filter(function (todo) {
            return todo.id !== id
        });
        let todosBackup = this.state.todosBackup.filter(function (todoBackup) {
            return todoBackup.id !== id
        });
        this.setState({
            todos: todos,
            todosBackup: todosBackup
        });
    }

    render() {

        let todos = this._formatTodosListing();

        return (

            <div className="App container">

                <input type="text" placeholder="Search" onChange={this.search.bind(this)}/>
                <table>
                    <thead>
                    <tr>
                        <th>ID</th>
                        <th>User</th>
                        <th>Title</th>
                        <th>Completed</th>
                        <th>Actions</th>
                    </tr>
                    </thead>
                    <tbody>
                    {todos}
                    </tbody>
                </table>
            </div>
        );
    }
}

//export default App;

ReactDOM.render(<App />, document.getElementById('root'));

// If you want your app to work offline and load faster, you can change
// unregister() to register() below. Note this comes with some pitfalls.
// Learn more about service workers: https://bit.ly/CRA-PWA
serviceWorker.unregister();
